#{{id}} .mypopup-body p {
  margin: 0;
  padding-left: 10px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

#{{id}} .mypopup-body p:before {
  display: none;
}

#{{id}} .mypopup-button {
  margin-left: 10px;
  border-radius: 20px;
}

#{{id}} .mypopup-modal {
  background: transparent;
}

#{{id}} .mypopup-body input[type=email] {
  font-size: 1.2em;
  padding: .9em 1.5em;
  border-radius: 6px;
  text-align: center;
}

#{{id}} .mypopup-body button {
  border: none;
  color: #000;
  background: #fff;
  font-size: .9em;
}

@media (min-width: 768px) {
  #{{id}} .h1, h1 {
    font-size: 3.2em;
    margin-bottom: 5vh;
  }
}

#{{id}} .mypopup-modal {
  background-image:linear-gradient(129deg, rgba(254,255,209,0.25) 0%, rgba(255,255,230,0) 50%, rgba(255,255,240,0) 55%, rgba(83,72,88,0.1) 100%);
}
#{{id}} .mypopup-body p { line-height: 1.4; margin: 8px 0; }
#{{id}} .mypopup-button { margin-top: 7px; }

#{{id}} .mypopup-body a { color: #fff; text-decoration: none; }
#{{id}} .mypopup-body a:before { content: \'\'; position: absolute; top: 0; right: 0; bottom: 0; left: 0; border-radius: 100px; }
#{{id}}  .mypopup-modal .mypopup-icon { transition: all .2s; }
#{{id}}  .mypopup-modal:hover .mypopup-icon { transform: scale(1.2); }
#{{id}}  .mypopup-modal-close { z-index: 100; }

#{{id}} .mypopup-body p { margin: 6px 0; }
#{{id}} .mypopup-icon { margin-top: 10px; animation: mypopup-bell 2s .7s ease-in-out infinite; transform-origin: 50% 4px; }

@keyframes mypopup-bell {
  0% { transform: rotate(0); }
  3% { transform: rotate(20deg); }
  6% { transform: rotate(-18deg); }
  9% { transform: rotate(24deg); }
  12% { transform: rotate(-22deg); }
  15% { transform: rotate(16deg); }
  18% { transform: rotate(-14deg); }
  21% { transform: rotate(12deg); }
  24% { transform: rotate(-10deg); }
  27% { transform: rotate(8deg); }
  30% { transform: rotate(-6deg); }
  33% { transform: rotate(4deg); }
  36% { transform: rotate(-2deg); }
  39% { transform: rotate(0deg); }
  42% { transform: rotate(0); }
  100% { transform: rotate(0); }
}

